mapper.select:
select cour_id, curr_id, year, semester,IF(stud_num is null, 0, stud_num)as stud_num,cour_name,
       type, exam_type, depa_name, credit, requirement, prerequisite, start_week, end_week, lect_id,
    lect_name, room_id, campus, building, door, slot_id, day, start_class, end_class, capacity
from curr_count natural right outer join class

    AND day = #{day}
    AND campus=#{campus}
    AND depa_name = #{depa_name}
    AND cour_id = #{cour_id}
    AND curr_id = #{curr_id}
    AND year = #{year}
    AND semester = #{semester}
    AND lect_id = #{lect_id}
    AND lect_name = #{lect_name}
    AND cour_name like concat(concat('%',#{cour_name}),'%')

select cour_id, curr_id, year, semester,IF(stud_num is null, 0, stud_num)as stud_num, cour_name,
type, exam_type, depa_name, credit, requirement, prerequisite, start_week, end_week, lect_id,
lect_name, room_id, campus, building, door, slot_id, day, start_class, end_class, capacity
from curr_count natural right outer join class

    (stud_num is null or stud_num < capacity)
    AND day = #{day}
    AND campus=#{campus}
    AND depa_name = #{depa_name}
    AND cour_id = #{cour_id}
    AND curr_id = #{curr_id}
    AND year = #{year}
    AND semester = #{semester}
    AND lect_id = #{lect_id}
    AND lect_name = #{lect_name}
    AND cour_name like concat(concat('%',#{cour_name}),'%')
